import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Preloader } from '../../Components';
import axios from 'axios';
import './NewsDetail.css';

const NewsDetail = () => {
  const { index } = useParams();
  const [news, setNews] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNewsDetail = async () => {
      try {
        const response = await axios.get(
          `https://newsapi.org/v2/top-headlines?category=health&country=us&apiKey=96d8094b9d584e4baa94603f7125144b`
        );
        const articles = response.data.articles;
        if (articles && articles.length > index) {
          setNews(articles[index]);
        } else {
          throw new Error('News not found');
        }
        setLoading(false);
      } catch (error) {
        console.log('Error fetching news detail:', error);
        setLoading(false);
      }
    };

    fetchNewsDetail();
  }, [index]);

  if (loading) {
    return <Preloader />;
  }

  if (!news) {
    return <div>Failed to fetch news detail.</div>;
  }

  // Inside the NewsDetail component

async function fetchNewsDetail() {
     try {
       const response = await axios.get(
         `https://newsapi.org/v2/top-headlines?category=health&country=us&apiKey=96d8094b9d584e4baa94603f7125144b`
       );
       const articles = response.data.articles;
       const news = articles[0]; // Assuming you want the first article

       const response2 = await axios.get(news.url); // Fetch the full content from the article URL
       const fullContent = response2.data; // Assuming the response contains the full content

       setNews({
         title: news.title,
         content: fullContent,
       });
     } catch (error) {
       console.error(error);
     }
   }

   useEffect(() => {
     fetchNewsDetail();
   }, []);


  return (
          // ...
          <section id="news-detail" data-stellar-background-ratio="0.5">
            <div className="container">
              <div className="row">
                <div className="col-md-8 col-sm-7">
                  <div className="news-detail-thumb">
                    <div className="news-image">
                      <img src={news.urlToImage} className="img-responsive" alt="" />
                    </div>
                    <h3>{news.title}</h3>
                    <p>{news.content}</p>
                    {news.quote && <blockquote>{news.quote}</blockquote>}
                    {news.additionalInfo && <p>{news.additionalInfo}</p>}
                    {news.listItems && (
                      <ul>
                        {news.listItems.map((item, index) => (
                          <li key={index}>{item}</li>
                        ))}
                      </ul>
                    )}
                    {news.moreInfo && <p>{news.moreInfo}</p>}
                    <div className="news-social-share">
                      <h4>Share this article</h4>
                      <a href="#" className="btn btn-primary">
                        <i className="fa fa-facebook"></i>Facebook
                      </a>
                      <a href="#" className="btn btn-success">
                        <i className="fa fa-twitter"></i>Twitter
                      </a>
                      <a href="#" className="btn btn-black">
                        <i className="fa fa-medium"></i>Medium
                      </a>
                    </div>
                  </div>
                </div>
                <div className="col-md-4 col-sm-5">
                  <div className="news-sidebar">
                    {news.author && (
                      <div className="news-author">
                        <h4>About the author</h4>
                        <p>{news.author}</p>
                      </div>
                    )}
                    {news.recentPosts && (
                      <div className="recent-post">
                        <h4>Recent Posts</h4>
                        {news.recentPosts.map((post, index) => (
                          <div className="media" key={index}>
                            <div className="media-object pull-left">
                              <a href="#">
                                <img src={post.image} className="img-responsive" alt="" />
                              </a>
                            </div>
                            <div className="media-body">
                              <h4 className="media-heading">
                                <a href="#">{post.title}</a>
                              </h4>
                            </div>
                          </div>
                        ))}
                      </div>
                    )}
                    {news.categories && (
                      <div className="news-categories">
                        <h4>Categories</h4>
                        {news.categories.map((category, index) => (
                          <li key={index}>
                            <a href="#">
                              <i className="fa fa-angle-right"></i> {category}
                            </a>
                          </li>
                        ))}
                      </div>
                    )}
                    <div className="news-ads sidebar-ads">
                      <h4>Sidebar Banner Ad</h4>
                    </div>
                    {news.tags && (
                      <div className="news-tags">
                        <h4>Tags</h4>
                        {news.tags.map((tag, index) => (
                          <li key={index}>
                            <a href="#">{tag}</a>
                          </li>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </section>
        );
};

export default NewsDetail;
