import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Preloader, Navbar, Footer, Slider, SignUp } from './Components';
import { Home, AboutUs, Team, News, Contact, Appointment, Header } from './Container';

import './App.css';
import NewsDetail from './Container/NewsDetail/NewsDetail';

const App = () => {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    // Check if the user is already authenticated
    const user = localStorage.getItem('user');
    if (user) {
      setAuthenticated(true);
    }
  }, []);

  const handleSignup = () => {
    // Perform signup logic and set authenticated to true
    setAuthenticated(true);
    // Save the user's authentication status
    localStorage.setItem('user', 'authenticated');
  };

  return (
    <Router>
      <div>
        <Preloader />
        <Navbar />
        <Routes>
          {!authenticated ? (
            <Route path="/" element={<SignUp onSignup={handleSignup} />} />
          ) : (
            <>
              <Route path="/" element={<Navigate to="/home" />} />
              <Route path="/home" element={<Home />} />
              <Route path="/about" element={<AboutUs />} />
              <Route path="/team" element={<Team />} />
              <Route path="/news" element={<News />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/appointment" element={<Appointment />} />
            </>
          )}
          {/* <Route path="/news-detail" element={<NewsDetail />} /> */}
          {/* <Route path="/news-detail/:index" element={<NewsDetail />} /> */}
        </Routes>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
